import { db } from "./firebase";

function Needies() {
  const [needies, setNeedies] = useState([]);

  useEffect(() => {
    db.collection("users").onSnapshot((snapshot) => {
      setNeedies(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          post: doc.data(),
        }))
